import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { environment } from './../environments/environment';
import { Product } from './Models/product';
import { ProductService } from './Services/product.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  products: Product[] = [];
  pageNumber = 1;
  pageSize = 4;
  sortField = 'Name';
  sortOrder = 'ASC';

 

  constructor(private productService: ProductService) {
  }

  title = 'angularapp';
  ngOnInit(): void {
    //this.fetchDataWithOracle();
    this.fetchDataWithMSSql();
  }

  fetchDataWithMSSql(): void {
    this.productService.getProductsWithMSSql()
      .subscribe(data => {
        this.products = data;
      });
  }

  fetchDataWithOracle(): void {
    this.productService.getProducts(this.pageNumber, this.pageSize, this.sortField, this.sortOrder)
      .subscribe(data => {
        this.products = data;
      });
  }

  onPageChange(page: number): void {
    this.pageNumber = page;
    this.fetchDataWithOracle();
  }

  onSort(field: string): void {
    if (field === this.sortField) {
      this.sortOrder = this.sortOrder === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortField = field;
      this.sortOrder = 'asc';
    }
    this.fetchDataWithOracle();
  }
}

<h1 id="tableLabel">Product List</h1>

<p>This component demonstrates fetching data from the server.</p>


<p *ngIf="!products"><em>Loading... ..</em></p>

<table *ngIf="products">
  <thead>
    <tr>
      <th>Name</th>
      <th>Description</th>
      <th>Category</th>
      <th>Price</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let product of products">
      <td>{{ product.name }}</td>
      <td>{{ product.description }}</td>
      <td>{{ product.category }}</td>
      <td>{{ product.price }}</td>
    </tr>
  </tbody>
</table>



